import React from 'react';
import './App.css';

// git project https://github.com/erikflowers/weather-icons
import 'weather-icons/css/weather-icons.css';

const API_KEY = "ef7b3e775066b56ae79afbd0b3068de3";


class Form extends React.Component {
  constructor(props){
    super(props)
  }

  render() {
    return (
      <form onSubmit={this.props.getWeather}>
        <input type="text" name="city" placeholder="City..."/>
        <input type="text" name="country" placeholder="Country..."/>
        <button>Get Weather</button>
      </form>
    )
  }
}


class App extends React.Component {
  constructor(props){
    super(props)
  }

  getWeather = async (e) => {
    // e.preventDefault();
    // `` is used instead of "" so that we can use ${} to specify our own API key, here we are just making a call to the specific URL
    const api_call = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=London,uk&APPID=${API_KEY}`);

    // converting the response to JSON format, converts the data got from API to a readable format
    const data = await api_call.json();
    // const data = "boo"

    console.log(data);
  }

  render() {
    return (
      <div>
        <h1>Works?</h1>
        <i className="wi wi-day-lightning"></i>
        <Form getWeather={this.getWeather}/>
      </div>
    )
  }
}

export default App;
