import React from 'react';
import './TodayWeather.css'

class TodayWeather extends React.Component {
  constructor(props){
    super(props)

    this.convertDate = this.convertDate.bind(this)
  }

  convertDate(dateISO) {
    const date1 = new Date(dateISO)
    const dateTimeFormat = new Intl.DateTimeFormat('en', { year: 'numeric', month: 'short', day: '2-digit' })
    const [{ value: month },,{ value: day },,{ value: year }] = dateTimeFormat .formatToParts(date)
    var pfix = ''
    var dateLong = ''

    if(day[1] === '1') { pfix = "st"}
    else if(day[1] === '2') { pfix = "nd"}
    else if(day[1] === '1') { pfix = "st"}
    else {pfix = "th"}

    if(day[0] === '0'){ dateLong = `${day[1]}${pfix} ${month} ${year}`}
    else { dateLong = `${day}${pfix} ${month} ${year}`}

    return dateLong
  }

  render() {
    return (
      <div className="main" style={{visibility: this.props.city ? 'visible' : 'hidden'}}>
        <img className="icon"
          src={
            this.props.data
              ? require(`../images/${this.props.data.icon}.svg`)
              : require('../images/01d.svg')
          }
          // style ensures that the element is visible only when the city is defined
          style={{
            visibility: this.props.city ? 'visible' : 'hidden',
            opacity: this.props.city ? '1' : '0',
            // width: "700px",
            // height: "700px"
          }}
        />

        <div className='today' style={{ visibility: this.props.city ? 'visible' : 'hidden', opacity: this.props.city ? '1' : '0'}}>
          <p>Today, {this.props.data ? this.props.data.date : ''}</p>
          <h1>{this.props.city}</h1>
          <p>Temperature: {this.props.data ? Math.round(this.props.data.temp) : 0}Â°C</p>
          <p>{this.props.data ? this.props.data.weather_desc.toLowerCase() : ''}</p>
        </div>
      </div>
    )
  }
}

export default TodayWeather;
