import React from 'react';
import './App.css';

// git project https://github.com/erikflowers/weather-icons
import 'weather-icons/css/weather-icons.css';

const API_KEY = "ef7b3e775066b56ae79afbd0b3068de3";


class App extends React.Component {
  constructor(props){
    super(props)
  }

  getWeather = async () => {
    // `` is used instead of "" so that we can use ${} to specify our own API key, here we are just making a call to the specific URL
    const api_call = await fetch(`api.openweathermap.org/data/2.5/weather?q=London,uk&APPID=${API_KEY}&units=metric`);

    // converting the response to JSON format, converts the data got from API to a readable format
  }

  render() {
    return (
      <div>
        <h1>Works?</h1>
        <i className="wi wi-day-lightning"></i>
      </div>
    )
  }
}

export default App;
