import React from 'react';
import './App.css';

// git project https://github.com/erikflowers/weather-icons
import 'weather-icons/css/weather-icons.css';

const API_KEY = "ef7b3e775066b56ae79afbd0b3068de3";

class App extends React.Component {
  constructor(props){
    super(props)

    this.state = {
      city: undefined,

      // days contain today and next 4 days
      // for each day there is: date, weather_desc, icon, temp
      days: new Array(5)
    }
  }

  handleChange(e) {
    this.setState({
      city: e.target.value
    });
  }

  render() {
    return (
      <div>
        <h1>Works?</h1>
        <i className="wi wi-day-lightning"></i>
        <input type="text" name="city" placeholder="City..." onChange={this.handleChange} value={this.state.city}/>
        <button onClick={this.makeApiCall.bind(this)}>Find</button>
      </div>
    )
  }
}

