import React from 'react';
import './App.css';

// git project https://github.com/erikflowers/weather-icons
import 'weather-icons/css/weather-icons.css';

const API_KEY = "ef7b3e775066b56ae79afbd0b3068de3";

class CityInput extends React.Component {
  constructor(props){
    super(props)
  }

  render() {
    // here e corresponds to the input box
    const onKlickHandler = async e => {
      e.persist();
      const eventKey = e.which ? e.which : e.keyCode;
      const city = e.target.value;

      // check if input contains only letters after Enter was pressed, 13 is the code for enter key
      if (eventKey === 13) {
        if (/^[a-zA-ZäöüÄÖÜß ]+$/.test(city)) {
          // adding the className loading so that the loading gif is displayed when api call is being made
          e.target.classList.add('loading');

          if (await this.props.makeApiCall(city)) e.target.placeholder = 'Enter a City...';
          else e.target.placeholder = 'City was not found, try again...';
        }
        // if input has any numbers
        else e.target.placeholder = 'Please enter a valid city name...';

        e.target.classList.remove('loading');
        e.target.value = '';
      }
    };

    return (
      <input
        className='city-input'
        type='text'
        placeholder='Enter a City...'
        onKeyPress={onKlickHandler}
      />
    );

  }
}




class App extends React.Component {
  constructor(props){
    super(props)

    this.state = {
      city: '',
      // days contain today and next 4 days
      // for each day there is: date, weather_desc, icon, temp
      days: new Array(5)
    }

    // this.handleChange = this.handleChange.bind(this);
  }


  // creates the day objects and updates the state
  updateState = data => {
    const city = data.city.name;
    const days = [];
    const dayIndices = this.getDayIndices(data);

    for (let i = 0; i < 5; i++) {
      days.push({
        date: data.list[dayIndices[i]].dt_txt,
        weather_desc: data.list[dayIndices[i]].weather[0].description,
        icon: data.list[dayIndices[i]].weather[0].icon,
        temp: data.list[dayIndices[i]].main.temp
      });
    }

    this.setState({
      city: city,
      days: days
    });
  };

  // tries to make an API call with the given city name and triggers state update
  makeApiCall = async city => {
    const api_data = await fetch(
      `https://api.openweathermap.org/data/2.5/forecast?q=${city}&APPID=${API_KEY}&units=metric`
    ).then(resp => resp.json());

    if (api_data.cod === '200') {
      await this.updateState(api_data);
      console.log(this.state.days)
      return true;
    } else return false;
  };

  // returns array with Indices of the next five days in the list
  // from the API data (every day at 12:00 pm)
  getDayIndices = data => {
    let dayIndices = [];
    dayIndices.push(0);

    let index = 0;
    let tmp = data.list[index].dt_txt.slice(8, 10);

    for (let i = 0; i < 4; i++) {
      while (
        tmp === data.list[index].dt_txt.slice(8, 10) ||
        data.list[index].dt_txt.slice(11, 13) !== '15'
      ) {
        index++;
      }
      dayIndices.push(index);
      tmp = data.list[index].dt_txt.slice(8, 10);
    }
    return dayIndices;
  };


  // handleChange(e) {
  //   this.setState({
  //     city: e.target.value
  //   });
  // }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <CityInput city={this.state.city} makeApiCall={this.makeApiCall.bind(this)}/>
        </header>
      </div>
    )
  }
}

export default App;
