import React from 'react';
import './App.css';

// git project https://github.com/erikflowers/weather-icons
import 'weather-icons/css/weather-icons.css';

const API_KEY = "ef7b3e775066b56ae79afbd0b3068de3";


class Form extends React.Component {
  constructor(props){
    super(props)
  }

  render() {
    return (
      <form onSubmit={this.props.getWeather}>
        <input type="text" name="city" placeholder="City..."/>
        <input type="text" name="country" placeholder="Country..."/>
        <button>Get Weather</button>
      </form>
    )
  }
}


class App extends React.Component {
  constructor(props){
    super(props)

    this.state = {
      temperature: undefined,
      city: undefined,
      country: undefined,
      humidity: undefined,
      description: undefined,
      error: undefined
    }
  }

  getWeather = async (e) => {
    e.preventDefault();

    // gets the user input value typed in city box
    const city = e.target.elements.city.value;
    const country = e.target.elements.country.value;

    // `` is used instead of "" so that we can use ${} to specify our own API key, here we are just making a call to the specific URL
    const api_call = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city},${country}&APPID=${API_KEY}&units=metric`);

    // converting the response to JSON format, converts the data got from API to a readable format
    const data = await api_call.json();

    console.log(data);

    this.setState({
      temperature: data.main.temp,
      city: data.name,
      country: data.sys.country,
      humidity: data.main.humidity,
      description: data.weather[0].description,
      error: ""
    });
  }

  render() {
    return (
      <div>
        <h1>Works?</h1>
        <i className="wi wi-day-lightning"></i>
        <Form getWeather={this.getWeather}/>
      </div>
    )
  }
}

export default App;
